    from_time_str = "2027-01-01 00:00:00"
    until_time_str = "2027-01-01 01:00:00"

    parent_network = ipaddress.ip_network(parent_prefix_str)

    from_time = datetime.strptime(from_time_str, "%Y-%m-%d %H:%M:%S").replace(tzinfo=timezone.utc)
