                stream.set_data_interface_option("singlefile", "upd-file", file_path)

                for rec in stream.records():
                    for elem in rec:
                        elem_dt = datetime.utcfromtimestamp(elem.time).replace(tzinfo=timezone.utc)
                        if elem_dt < from_time or elem_dt > until_time:
                            continue

                        if elem.type == 'A':
                            prefix_str = elem.fields.get('prefix')
                            as_path_str = elem.fields.get('as-path', '')
                            as_path_list = as_path_str.split()

                            if prefix_str and as_path_list:
                                try:
                                    announced_net = ipaddress.ip_network(prefix_str)
                                except ValueError:
                                    continue

                                origin_as = as_path_list[-1]

                                # Check if it's the parent prefix itself
                                if announced_net == parent_network:
                                    parent_origin_as = origin_as
                                else:
                                    # Check if it's a subprefix of the parent
                                    if announced_net.subnet_of(parent_network) and announced_net != parent_network:
                                        subprefix_origins[prefix_str].add(origin_as)

    results = []
    for spfx, origins in subprefix_origins.items():
        results.append({
            'subprefix': spfx,
            'origin_as_set': list(origins),
            'matches_parent_origin': (parent_origin_as in origins) if parent_origin_as else False
        })

    if results:
        df_results = pd.DataFrame(results)
        print("Subprefix Deaggregation for 10.1.0.0/16")
        print(df_results)
    else:
